mapper.select:
select f.applyid, u.username, u.profileimgurl, u.uid, ( select t.name from team t, teammember tm where t.teamid = tm.teamid and tm.uid = u.uid ) as teamname, ( select count(*) from usermission um where um.uid= u.uid ) as usermissioncount, ( select sum(grade +
case when um.passflag='Y' then um.passgrade else 0 end
) from usermission um where um.uid= u.uid ) as score, ( select count(*) from followmanage fw where fw.followuid = u.uid ) as followercount, ( select count(*) from followmanage fw where fw.uid = u.uid ) as followingcount, ( select count(*) from board bd where bd.writeuid = u.uid ) as mecommentcount, ( select count(*) from board bd where bd.uid = u.uid ) as commentcount, f.creationdate, f.flag from user_friend_apply f, user u where 1=1 and f.flag = #{param2} and f.requid = u.uid and f.resuid = #{param1}
union ALL select f.applyid, u.username, u.profileimgurl, u.uid, ( select t.name from team t, teammember tm where t.teamid = tm.teamid and tm.uid = u.uid ) as teamname, ( select count(*) from usermission um where um.uid= u.uid ) as usermissioncount, ( select sum(grade +
case when um.passflag='Y' then um.passgrade else 0 end
) from usermission um where um.uid= u.uid ) as score, ( select count(*) from followmanage fw where fw.followuid = u.uid ) as followercount, ( select count(*) from followmanage fw where fw.uid = u.uid ) as followingcount, ( select count(*) from board bd where bd.writeuid = u.uid ) as mecommentcount, ( select count(*) from board bd where bd.uid = u.uid ) as commentcount, f.creationdate, f.flag from user_friend_apply f, user u where 1=1 and f.flag = #{param2} and f.resuid = u.uid and f.requid = #{param1}

mapper.update:
update user_friend_apply set
    flag = #{flag},
    updatedate = now()
where 1=1
and requid = #{requid}
and resuid = #{resuid}

