mapper.update:
         update user_mission_pass SET
              
              failuredisp = #{failuredisp},
	          
              
              inscomment = #{inscomment},
              
              
              status = #{status},
              
              
              passflag = #{passflag},
              
               updatedate = now()
        where 1=1
        and  passid = #{passid}
        
             and uid = #{uid}
        
        
              and usermissionid = #{usermissionid}

mapper.select:
select passid, ump.seq, u.username, u.profileimgurl as userimge, ump.creationdate as change_creationdate, mi.youtubeaddr as missionvideo, umi.youtubeaddr as uservideo, mi.missionname, (select t.name from team t, teammember tm where t.teamid = tm.teamid and tm.uid = u.uid and authority='MEMBER' ) as teamname, ( select sum(sumi.grade +
case when sumi.passflag='Y' then sumi.passgrade else 0 end
) from usermission sumi where sumi.uid = u.uid ) as totalscore, ump.status, ump.instructorid, ump.usermissionid, ump.uid, ump.cashpoint, ump.creationdate, ump.inscomment, ump.failuredisp, ump.passflag, ump.updatedate, ( select name from mission_type ty where ty.typeid = mi.typeid ) as missiontype from user_mission_pass ump, mission mi, usermission umi, user u where ump.uid = u.uid and ump.usermissionid = umi.usermissionid and mi.missionid = ump.missionid and ump.status in ('SUCCESS','REJECT')
and ump.passid=#{passid}
and ump.instructorid=#{instructorid}
and ump.usermissionid=#{usermissionid}
and ump.uid=#{uid}

select passid, ump.seq, u.username, u.profileimgurl as userimge, ump.creationdate as change_creationdate, mi.youtubeaddr as missionvideo, umi.youtubeaddr as uservideo, mi.missionname, (select t.name from team t, teammember tm where t.teamid = tm.teamid and tm.uid = u.uid and authority='MEMBER' ) as teamname, ( select sum(sumi.grade +
case when sumi.passflag='Y' then sumi.passgrade else 0 end
) from usermission sumi where sumi.uid = u.uid ) as totalscore, ump.status, ump.instructorid, ump.usermissionid, ump.uid, ump.cashpoint, ump.creationdate, ump.inscomment, ump.failuredisp, ump.passflag, ump.updatedate, ( select name from mission_type ty where ty.typeid = mi.typeid ) as missiontype from user_mission_pass ump, mission mi, usermission umi, user u where ump.uid = u.uid and ump.usermissionid = umi.usermissionid and mi.missionid = ump.missionid
and ump.status=#{status}
and ump.passid=#{passid}
and ump.instructorid=#{instructorid}
and ump.usermissionid=#{usermissionid}
and ump.uid=#{uid}

select ums.*,
(
select passgrade
from mission mi
where mi.missionid = ums.missionid
) as passgrade,
ins.profileimgurl as insimge,
ins.name as insname,
(
select mt.name
from mission mi,
mission_type mt
where mi.typeid = mt.typeid
and mi.missionid = ums.missionid
) as missiontype
from user_mission_pass ums,
     instructor ins
where ins.instructorid = ums.instructorid

       and  ums.uid = #{uid}


       and  ums.usermissionid = #{usermissionid}


       and  ums.missionid = #{missionid}


       and ums.evaltype = #{evaltype}

select max(seq)
from user_mission_pass
where 1=1

       and uid = #{uid}


       and missionid = #{missionid}


       and usermissionid = #{usermissionid}
