mapper.select:
select t.name as teamname, u.uid, u.username, u.profileimgurl, sum(umi.grade +
case when umi.passflag='Y' then umi.passgrade else 0 end
) as totalscore from usermission umi, user u, teammember tm, team t where u.uid = umi.uid and u.uid = tm.uid and tm.teamid = t.teamid and t.teamid = ( select stm.teamid from teammember stm where stm.uid = #{uid} ) group by u.uid,u.username,u.profileimgurl,t.teamid,t.name
totalscore desc
LIMIT #{queryRow}

select u.uid, u.username, u.profileimgurl, sum(umi.grade +
case when umi.passflag='Y' then umi.passgrade else 0 end
) as totalscore, ( select t.name from team t, teammember tm where t.teamid = tm.teamid and tm.uid = u.uid ) as teamname from usermission umi, user u, ( select resuid as uid from user_friend_apply where flag='ACCEPT' and requid=#{uid} union all select requid as uid from user_friend_apply where flag='ACCEPT' and resuid=#{uid} union all select uid from user where uid = #{uid} ) fr where u.uid = umi.uid and u.uid = fr.uid group by u.uid,u.username,u.profileimgurl
totalscore desc
LIMIT #{queryRow}
