mapper.select:
select t.*
from team t,
     teammember tm
where t.teamid = tm.teamid
and tm.uid=#{param1}

select ta.applyid, ta.instructorid, ta.uid, u.username, t.teamid, u.profileimgurl as userimge, ta.approval, ( select sum(sumi.grade +
case when sumi.passflag='Y' then sumi.passgrade else 0 end
) from usermission sumi where sumi.uid = u.uid ) as totalscore, ta.creationdate from team_apply ta, team t, user u where 1=1 and ta.teamid = t.teamid and ta.uid = u.uid and t.instructorid = #{param1} and ta.approval= #{param2}

select tm.*,
       ir.name as instructorname
from team tm,
     instructor ir
where tm.instructorid = ir.instructorid

       and tm.teamid = #{teamid}


       and tm.instructorid = #{instructorid}


       and tm.name like CONCAT('%',#{name},'%')


       and tm.enabled =#{enabled}

select tm.*,
       ir.name as instructorname
from team tm,
     instructor ir
where tm.instructorid = ir.instructorid

       and tm.teamid = #{teamid}


       and tm.instructorid = #{instructorid}


       and tm.name like CONCAT('%',#{name},'%')


       and tm.enabled =#{enabled}

LIMIT 1

mapper.update:
update team_apply
description = #{description}, approval = #{approval}, enabled = #{enabled}, reply = #{reply}, updatedate = now()
where applyid = #{applyid}

mapper.select:
select ta.*,
       u.username,
(select name
from team t
where t.teamid = ta.teamid) as teamname
from user u,
     team_apply ta
where u.uid = ta.uid

       and ta.applyid = #{applyid}


       and ta.teamid = #{teamid}


       and ta.instructorid = #{instructorid}


       and ta.uid = #{uid}


       and ta.approval = #{approval}


       and ta.enabled = #{enabled}

select count(*)
from team_apply ta
where 1=1

       and ta.applyid = #{applyid}


       and ta.teamid = #{teamid}


       and ta.instructorid = #{instructorid}


       and ta.uid = #{uid}


       and ta.approval = #{approval}


       and ta.enabled = #{enabled}

LIMIT 1

mapper.delete:
              delete from team_apply
              where 1=1
              
                     and applyid = #{applyid}
              
              
              and uid = #{uid}
              
              
              and instructorid = #{instructorid}

mapper.select:
select ta.*, u.username,
(select name
from team t
where t.teamid = ta.teamid) as teamname
from user u,
          team_apply ta
where u.uid = ta.uid

       and ta.applyid = #{applyid}


       and ta.teamid = #{teamid}


       and ta.instructorid = #{instructorid}


       and ta.uid = #{uid}


       and ta.approval = #{approval}


       and ta.enabled = #{enabled}

LIMIT 1

mapper.update:
update teammember

       enabled = #{enabled}
       updatedate = now()

where 1=1

       and   teammemberId = #{teammemberId}


       and   uid = #{uid}

mapper.select:
              select te.name as teamname,

                     u.username,
                     me.*
              from team te,
                   teammember me,
                   user u
              where te.teamid = me.teamid
              and u.uid = me.uid
              
                     and   me.teamid = #{teamid}
              
              
                     and   me.uid = #{uid}
              
              
                     and   me.enabled = #{enabled}

select te.name as teamname,
u.username,
me.*
from team te,
     teammember me,
     user u
where te.teamid = me.teamid
and u.uid = me.uid

       and   me.teamid = #{teamid}


       and   me.uid = #{uid}


       and   me.enabled = #{enabled}
