mapper.select:
select umi.*, u.username, u.profileimgurl, ( select t.name from team t, teammember tm where t.teamid = tm.teamid and tm.uid = u.uid ) as teamname, ( select count(*) from mybookmark book where book.usermissionid = umi.usermissionid ) as bookmarkcount, ( select count(*) from mybookmark mk where mk.usermissionid = umi.usermissionid and uid = #{uid} ) mycheck, ( select count(*) from board bd where bd.usermissionid= umi.usermissionid and bd.uid = umi.uid ) boardcount, umi.passflag, 1 mycheck, ( select mi.missionname from mission mi where mi.missionid=umi.missionid ) as missionname, ( select mt.name from mission_type mt, mission mi where mt.typeid=mi.typeid and mi.missionid = umi.missionid ) as missiontype from usermission umi,user u where umi.uid = u.uid
and umi.missionid = #{missionid}
and umi.uid != #{uid}
and umi.passflag = #{passflag}
order by creationdate desc LIMIT #{queryRow}

select umi.*, u.username, u.profileimgurl, ( select t.name from team t, teammember tm where t.teamid = tm.teamid and tm.uid = u.uid ) as teamname, ( select count(*) from mybookmark book where book.usermissionid = umi.usermissionid ) as bookmarkcount, ( select count(*) from board bd where bd.usermissionid= umi.usermissionid and bd.uid = umi.uid ) boardcount, umi.passflag, ( select mi.missionname from mission mi where mi.missionid=umi.missionid ) as missionname, ( select count(*) from mybookmark mk where mk.usermissionid = umi.usermissionid and uid = #{uid} ) mycheck, umi.grade+( case when umi.passflag='Y' then umi.passgrade else 0 end ) as totalscore, ( select mt.name from mission_type mt, mission mi where mt.typeid=mi.typeid and mi.missionid = umi.missionid ) as missiontype, mi.sequence as sequence from usermission umi, user u, mission mi where umi.uid = u.uid and umi.missionid = mi.missionid
and umi.usermissionid = #{usermissionid}
and umi.missionid = #{missionid}
and umi.uid = #{uid}
and umi.uploadflag = #{uploadflag}
and umi.passflag = #{passflag}

select umi.*, u.username, u.profileimgurl, ( select t.name from team t, teammember tm where t.teamid = tm.teamid and tm.uid = u.uid ) as teamname, ( select count(*) from mybookmark book where book.usermissionid = umi.usermissionid ) as bookmarkcount, ( select count(*) from board bd where bd.usermissionid= umi.usermissionid and bd.uid = umi.uid ) boardcount, umi.passflag, ( select mi.missionname from mission mi where mi.missionid=umi.missionid ) as missionname, ( select count(*) from mybookmark mk where mk.usermissionid = umi.usermissionid and uid = #{uid} ) mycheck, umi.grade+( case when umi.passflag='Y' then umi.passgrade else 0 end ) as totalscore, ( select mt.name from mission_type mt, mission mi where mt.typeid=mi.typeid and mi.missionid = umi.missionid ) as missiontype, mi.sequence as sequence from usermission umi, user u, mission mi where umi.uid = u.uid and umi.missionid = mi.missionid
and umi.usermissionid = #{usermissionid}
and umi.missionid = #{missionid}
and umi.uid = #{uid}
and umi.uploadflag = #{uploadflag}
and umi.passflag = #{passflag}
LIMIT 1

select count(*)
from usermission
where 1=1

    and umi.usermissionid = #{usermissionid}


    and umi.missionid = #{missionid}


    and umi.uid = #{uid}


    and umi.uploadflag = #{uploadflag}


    and umi.passflag = #{passflag}

LIMIT 1

select count(*)
from usermission_eval ue
where 1=1

    and ue.uid = #{uid}


    and ue.instructorid = #{instructorid}


    and ue.score = #{score}

LIMIT 1

        select sum(evalid) as sum,
               avg(score)  as avg
        from usermission_eval ue
        where 1=1
        
            and ue.uid = #{uid}
        
        
            and ue.usermissionid = #{usermissionid}
