mapper.select:
select ty.name as typename,
mi.*,
pt.getpoint,
pt.escapepoint,
(
select count(*)
from mission_open_history moh
where moh.uid = #{uid}
and   moh.missionid= mi.missionid
) opencount,
(
select count(*)
from usermission um
where um.missionid = mi.missionid
and   um.uid =  #{uid}
) uploadcount,
(
select count(*)
from usermission um
where um.missionid = mi.missionid
and   um.passflag='Y'
and   um.uid =  #{uid}
) missionpasscount
,enname
,endescription
,enprecon
,cnname
,cndescription
,cnprecon
from mission mi,
mission_type ty,
pointmanagement pt
where ty.typeid = mi.typeid
and   mi.missionid = pt.missionid

    and mi.categoryid = #{categoryid}


    and mi.typeid = #{typeid}


    and mi.missionname = #{missionname}


    and mi.enabled = #{enabled}


    and mi.youtubeaddr = #{youtubeaddr}


    and ty.name = #{typename}

order by sequence DESC

select ty.name as typename,
mi.*,
pt.getpoint,
pt.escapepoint,
(
select count(*)
from mission_open_history moh
where moh.uid = #{uid}
and   moh.missionid= mi.missionid
) opencount,
(
select count(*)
from usermission um
where um.missionid = mi.missionid
and   um.uid =  #{uid}
) uploadcount,
(
select count(*)
from usermission um
where um.missionid = mi.missionid
and   um.passflag='Y'
and   um.uid =  #{uid}
) as missionpasscount
,enname
,endescription
,enprecon
,cnname
,cndescription
,cnprecon
from mission mi,
mission_type ty,
pointmanagement pt
where ty.typeid = mi.typeid
and   mi.missionid = pt.missionid

    and mi.missionid = #{missionid}


    and mi.categoryid = #{categoryid}


    and mi.typeid = #{typeid}


    and mi.missionname = #{missionname}


    and mi.enabled = #{enabled}


    and mi.youtubeaddr = #{youtubeaddr}

LIMIT 1

select *
from mission_eval me
where 1=1

    and me.evalid = #{evalid}


    and me.missionid = #{missionid}


    and me.uid = #{uid}


    and me.comment like CONCAT('%',#{comment},'%')


    and me.userscore = #{userscore}

